sysauto_script.script:
var howLongToClose = gs.getProperty('x_58872_needit.autoCloseOverdue');

//Query (interrogo) il database sui NeedIt Task records con il field Due date con valori che siano vecchi di x giorni (x è il valore settato nelle prop ....autoCloseOverdue)
//App property: dammi solo task con stato aperto o in progress, chiudi il task in quel caso e aggiorna le work notes.

var overdueNITasks = new GlideRecord('x_58872_needit_needit_task');

overdueNITasks.addQuery('due_date','<=',gs.daysAgo(howLongToClose)); //sta chiedendo al db se la prima variabile (field della tabella) è <= della terza
overdueNITasks.addQuery('state','<',3);
overdueNITasks.query();

//Scrivi un messaggio di log per ogni long overdue NITask. Aggiungi una nota alle work notes, cambia lo stato a incompleto, updata il record

while(overdueNITasks.next()){
	gs.info("Auto close NeedIt Task record = " + overdueNITasks.number);
	overdueNITasks.work_notes = "Record closed because it was too far past due date.";
	overdueNITasks.state = 4;
	overdueNITasks.update();
}
